import React, { useEffect, useState } from "react";
import API from "../services/api";
import { useNavigate } from "react-router-dom";
import "../styles/AdminDashboard.css";

export default function AdminDashboard() {
  const navigate = useNavigate();
  const [questions, setQuestions] = useState([]);
  const [results, setResults] = useState([]);
  const [activeTab, setActiveTab] = useState("questions");
  const [form, setForm] = useState({
    questionText: "",
    answerText: "",
  });
  const [file, setFile] = useState(null);
  const [loading, setLoading] = useState(false);
  const [loadingResults, setLoadingResults] = useState(false);
  const [editingId, setEditingId] = useState(null);

  // üß© Test control state
  const [testControl, setTestControl] = useState({
    isActive: false,
    questionLimit: 10,
    timeLimit: 30,
  });

  // üü¢ Check admin login
  useEffect(() => {
    const token = localStorage.getItem("adminToken");
    if (!token) navigate("/admin/login");
  }, [navigate]);

  // üü¢ Initial fetch
  useEffect(() => {
    fetchQuestions();
    fetchResults();
    fetchTestControl();
  }, []);

  // ‚úÖ Fetch Test Control
  const fetchTestControl = async () => {
    try {
      const res = await API.testcontrol.get();
      if (res.data) setTestControl(res.data);
    } catch (err) {
      console.error("Error fetching test control:", err);
    }
  };

  // ‚úÖ Update Test Control + Activate users
  const handleStartTest = async () => {
    try {
      await API.testcontrol.update({
        isActive: true,
        questionLimit: testControl.questionLimit,
        timeLimit: testControl.timeLimit,
      });

      const res = await API.user.activateAll();
      alert(res.data.message || "‚úÖ Test started, all users activated!");
      await fetchWaitingUsers();
    } catch (err) {
      console.error("‚ùå Failed to start test:", err);
      alert("‚ùå Failed to start test.");
    }
  };

  // ‚úÖ Stop test
  const handleStopTest = async () => {
    try {
      await API.testcontrol.update({ isActive: false });
      alert("‚èπÔ∏è Test stopped successfully!");
      await fetchTestControl();
    } catch (err) {
      console.error("‚ùå Error stopping test:", err);
      alert("Failed to stop test.");
    }
  };

  // ‚úÖ Delete individual user by ID
  const handleDeleteUser = async (userId, userName) => {
    if (!window.confirm(`Are you sure you want to delete ${userName}?`)) return;
    
    try {
      const res = await API.waitingUsers.delete(userId);
      alert(res.data.message || "‚úÖ User deleted successfully!");
      await fetchWaitingUsers();
    } catch (err) {
      console.error("‚ùå Error deleting user:", err);
      alert(err.response?.data?.error || "Failed to delete user.");
    }
  };

  // ‚úÖ Clear all users with ONE CLICK
  const handleClearAllUsers = async () => {
    if (!window.confirm("‚ö†Ô∏è Are you sure you want to delete ALL waiting users? This action cannot be undone!")) return;
    
    try {
      const res = await API.waitingUsers.deleteAll();
      alert(res.data.message || "‚úÖ All users cleared successfully!");
      await fetchWaitingUsers();
    } catch (err) {
      console.error("‚ùå Error clearing all users:", err);
      alert(err.response?.data?.error || "Failed to clear all users.");
    }
  };

  // ‚úÖ Fetch Questions
  const fetchQuestions = async () => {
    try {
      const res = await API.questions.getAll();
      const data = Array.isArray(res.data)
        ? res.data
        : res.data.questions || [];
      setQuestions(data);
    } catch (err) {
      console.error(err);
      alert("‚ùå Failed to load questions.");
    }
  };

 // ‚úÖ Fetch Results
const fetchResults = async () => {
  setLoadingResults(true);
  try {
    const res = await API.tests.getAll();
    setResults(res.data || []);
  } catch (err) {
    console.error(err);
    alert("‚ùå Failed to load test results.");
  } finally {
    setLoadingResults(false);
  }
};


  // üü¢ Waiting Users
  const [waitingUsers, setWaitingUsers] = useState([]);
  const [waitingCount, setWaitingCount] = useState(0);

  const fetchWaitingUsers = async () => {
    try {
      const res = await API.waitingUsers.getAll();
      setWaitingUsers(res.data || []);
      setWaitingCount(res.data?.length || 0);
    } catch (err) {
      console.error("‚ùå Error fetching waiting users:", err);
    }
  };

  // üîÑ Periodically fetch users
  useEffect(() => {
    fetchWaitingUsers();
    const interval = setInterval(fetchWaitingUsers, 5000);
    return () => clearInterval(interval);
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("adminToken");
    navigate("/admin/login");
  };

  const handleViewValidation = (id) => {
    navigate(`/admin/result/${id}`);
  };

  // ‚úÖ Add / Update Question
  const handleAddOrUpdate = async (e) => {
    e.preventDefault();

    if (!form.questionText.trim()) {
      return alert("Please enter question text.");
    }

    setLoading(true);
    try {
      const payload = {
        questionType: "Theory",
        questionText: form.questionText,
        correctAnswer: form.answerText,
      };

      if (editingId) {
        await API.questions.update(editingId, payload);
        alert("‚úÖ Question updated successfully!");
      } else {
        await API.questions.create(payload);
        alert("‚úÖ Question added successfully!");
      }

      setForm({ questionText: "", answerText: "" });
      setEditingId(null);
      await fetchQuestions();
    } catch (err) {
      console.error(err);
      alert(err.response?.data?.error || "‚ùå Operation failed.");
    } finally {
      setLoading(false);
    }
  };

  const handleEdit = (q) => {
    setEditingId(q._id);
    setForm({
      questionText: q.questionText,
      answerText: q.correctAnswer || "",
    });
    window.scrollTo({ top: 0, behavior: "smooth" });
  };

  const handleDelete = async (id) => {
    if (!window.confirm("Are you sure you want to delete this question?"))
      return;
    try {
      await API.questions.delete(id);
      alert("üóëÔ∏è Question deleted successfully!");
      await fetchQuestions();
    } catch (err) {
      console.error(err);
      alert("‚ùå Failed to delete question.");
    }
  };

  const handleUpload = async () => {
    if (!file) return alert("Please select a file to upload.");
    const fd = new FormData();
    fd.append("file", file);

    try {
      const res = await API.questions.uploadDoc(fd);
      alert(res.data.message || "‚úÖ File uploaded successfully!");
      await fetchQuestions();
    } catch (err) {
      console.error(err);
      alert(err.response?.data?.error || "‚ùå File upload failed.");
    }
  };

  return (
    <div className="admin-dashboard">
      <div className="admin-header">
        <h2>Admin Dashboard</h2>
        <button className="logout-btn" onClick={handleLogout}>
          Logout
        </button>
      </div>

      {/* Tabs */}
      <div className="tab-buttons">
        <button
          className={`tab-btn ${activeTab === "questions" ? "active" : ""}`}
          onClick={() => setActiveTab("questions")}
        >
          üß© Questions
        </button>
        <button
          className={`tab-btn ${activeTab === "results" ? "active" : ""}`}
          onClick={() => setActiveTab("results")}
        >
          üìä Test Results
        </button>
      </div>

      {activeTab === "questions" ? (
        <div className="tab-content">
          {/* ‚úÖ Test Control Panel */}
          <div className="card form-section">
            <h5>üïπÔ∏è Test Control Panel</h5>

            {/* ‚úÖ Live Waiting Users Section */}
            <div className="waiting-users-section">
              <div className="waiting-header">
                <div className="waiting-title-wrapper">
                  <svg className="waiting-icon" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                    <path d="M12 8V12L15 15M21 12C21 16.9706 16.9706 21 12 21C7.02944 21 3 16.9706 3 12C3 7.02944 7.02944 3 12 3C16.9706 3 21 7.02944 21 12Z" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                  </svg>
                  <h5 className="waiting-title">Live Waiting Users</h5>
                  <span className="waiting-badge">{waitingCount}</span>
                </div>
                
                <div className="delete-actions">
                  <button 
                    className="delete-btn delete-all" 
                    onClick={handleClearAllUsers}
                    title="Clear All Users - One Click Delete"
                  >
                    <svg viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                      <path d="M19 7L18.1327 19.1425C18.0579 20.1891 17.187 21 16.1378 21H7.86224C6.81296 21 5.94208 20.1891 5.86732 19.1425L5 7M10 11V17M14 11V17M15 7V4C15 3.44772 14.5523 3 14 3H10C9.44772 3 9 3.44772 9 4V7M4 7H20" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                    </svg>
                    Clear All Users
                  </button>
                </div>
              </div>

              {waitingUsers.length === 0 ? (
                <div className="empty-state">
                  <svg className="empty-icon" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                    <path d="M20 21V19C20 17.9391 19.5786 16.9217 18.8284 16.1716C18.0783 15.4214 17.0609 15 16 15H8C6.93913 15 5.92172 15.4214 5.17157 16.1716C4.42143 16.9217 4 17.9391 4 19V21M16 7C16 9.20914 14.2091 11 12 11C9.79086 11 8 9.20914 8 7C8 4.79086 9.79086 3 12 3C14.2091 3 16 4.79086 16 7Z" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                  </svg>
                  <p className="empty-text">No users are waiting currently</p>
                </div>
              ) : (
                <div className="users-table-wrapper">
                  <table className="users-table">
                    <thead>
                      <tr>
                        <th>#</th>
                        <th>Full Name</th>
                        <th>Email</th>
                        <th>Status</th>
                        <th>Action</th>
                      </tr>
                    </thead>
                    <tbody>
                      {waitingUsers.map((user, index) => (
                        <tr key={user._id} className="user-row" style={{ animationDelay: `${index * 0.05}s` }}>
                          <td className="user-number">{index + 1}</td>
                          <td className="user-name">{user.fullName}</td>
                          <td className="user-email">{user.email}</td>
                          <td>
                            <span className={`status-badge status-${user.status.toLowerCase()}`}>
                              {user.status}
                            </span>
                          </td>
                          <td>
                            <button 
                              className="delete-user-btn"
                              onClick={() => handleDeleteUser(user._id, user.fullName)}
                              title="Delete User"
                            >
                              <svg viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                                <path d="M19 7L18.1327 19.1425C18.0579 20.1891 17.187 21 16.1378 21H7.86224C6.81296 21 5.94208 20.1891 5.86732 19.1425L5 7M10 11V17M14 11V17M15 7V4C15 3.44772 14.5523 3 14 3H10C9.44772 3 9 3.44772 9 4V7M4 7H20" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                              </svg>
                            </button>
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              )}
            </div>

            <div className="mb-3">
              <label>Number of Questions</label>
              <input
                type="number"
                className="form-control"
                value={testControl.questionLimit}
                onChange={(e) =>
                  setTestControl({
                    ...testControl,
                    questionLimit: e.target.value,
                  })
                }
              />
            </div>

            <div className="mb-3">
              <label>Time Limit (minutes)</label>
              <input
                type="number"
                className="form-control"
                value={testControl.timeLimit}
                onChange={(e) =>
                  setTestControl({
                    ...testControl,
                    timeLimit: e.target.value,
                  })
                }
              />
            </div>

            <div className="mb-3">
              <label>Status: </label>
              <span
                className={`ms-2 fw-bold ${
                  testControl.isActive ? "text-success" : "text-danger"
                }`}
              >
                {testControl.isActive ? "Active" : "Inactive"}
              </span>
            </div>

            <div className="d-flex gap-2">
              <button className="btn btn-success" onClick={handleStartTest}>
                ‚ñ∂Ô∏è Start Test
              </button>
              <button className="btn btn-danger" onClick={handleStopTest}>
                ‚èπÔ∏è Stop Test
              </button>
            </div>
          </div>

          {/* ‚úÖ Question Form */}
          <div className="card form-section mt-4">
            <h5>{editingId ? "‚úèÔ∏è Edit Question" : "‚ûï Add / Upload Questions"}</h5>

            <form onSubmit={handleAddOrUpdate}>
              <div className="mb-3">
                <label className="form-label">Question Text</label>
                <textarea
                  className="form-control"
                  rows={3}
                  value={form.questionText}
                  onChange={(e) =>
                    setForm({ ...form, questionText: e.target.value })
                  }
                  placeholder="Enter question here..."
                />
              </div>

              <div className="mb-3">
                <label className="form-label">Answer Text</label>
                <textarea
                  className="form-control"
                  rows={3}
                  value={form.answerText}
                  onChange={(e) =>
                    setForm({ ...form, answerText: e.target.value })
                  }
                  placeholder="Enter answer here..."
                />
              </div>

              <div className="d-flex gap-2">
                <button
                  className="btn btn-success"
                  type="submit"
                  disabled={loading}
                >
                  {loading
                    ? "Saving..."
                    : editingId
                    ? "Update Question"
                    : "Add Question"}
                </button>
                <button
                  type="button"
                  className="btn btn-secondary"
                  onClick={() => {
                    setForm({ questionText: "", answerText: "" });
                    setEditingId(null);
                  }}
                >
                  Clear
                </button>
              </div>
            </form>

            <hr className="my-4" />

            {/* ‚úÖ Upload Section */}
            <div className="upload-section">
              <label className="form-label">Upload File (.txt / .docx)</label>
              <input
                type="file"
                className="form-control"
                accept=".txt,.docx"
                onChange={(e) => setFile(e.target.files[0])}
              />
              <div className="mt-2">
                <button className="btn btn-primary" onClick={handleUpload}>
                  Upload
                </button>
              </div>
            </div>
          </div>

          {/* ‚úÖ Available Questions */}
          <div className="card available-questions mt-4">
            <h5>Available Questions</h5>

            {Array.isArray(questions) && questions.length === 0 && (
              <div className="text-muted">No questions yet</div>
            )}

            {Array.isArray(questions) &&
              questions.map((q, idx) => (
                <div key={q._id} className="question-item">
                  <strong>Q{idx + 1}:</strong> {q.questionText}
                  {q.correctAnswer && (
                    <div className="small text-success">
                      <b>Answer:</b> {q.correctAnswer}
                    </div>
                  )}
                  <div className="action-buttons">
                    <button
                      className="btn btn-sm btn-warning"
                      onClick={() => handleEdit(q)}
                    >
                      Edit
                    </button>
                    <button
                      className="btn btn-sm btn-danger"
                      onClick={() => handleDelete(q._id)}
                    >
                      Delete
                    </button>
                  </div>
                </div>
              ))}
          </div>
        </div>
      ) : (
        // ‚úÖ Test Results Tab
        <div className="tab-content">
          <div className="card results-section">
            <h5>Candidate Test Results</h5>
            {loadingResults ? (
              <div>Loading test results...</div>
            ) : results.length === 0 ? (
              <div className="text-muted">No test results yet</div>
            ) : (
              <table className="results-table">
                <thead>
                  <tr>
                    <th>Name</th>
                    <th>Email</th>
                    <th>Total</th>
                    <th>Correct</th>
                    <th>Score %</th>
                    <th>Status</th>
                    <th>Submitted At</th>
                    <th>Action</th>
                  </tr>
                </thead>
                <tbody>
                  {results.map((res, idx) => (
                    <tr key={idx}>
                      <td>{res.name}</td>
                      <td>{res.email}</td>
                      <td>{res.totalQuestions}</td>
                      <td>{res.correctAnswers}</td>
                      <td>{res.scorePercent}%</td>
                      <td>
                        <span
                          style={{
                            color:
                              res.status === "Validated" ? "green" : "orange",
                            fontWeight: 600,
                          }}
                        >
                          {res.status || "Validation Pending"}
                        </span>
                      </td>
                      <td>{new Date(res.submittedAt).toLocaleString()}</td>
                      <td>
                        <button
                          className="btn btn-sm btn-primary"
                          onClick={() => handleViewValidation(res._id)}
                        >
                          View / Validate
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            )}
          </div>
        </div>
      )}
    </div>
  );
}

